static int 
dtb_sdtfprop_moddialog_create(
    DtbSdtfpropModdialogInfo instance,
    Widget parent
)
{
    XmString	label_xmstring = NULL;
    Display	*display= (parent == NULL? NULL:XtDisplay(parent));
    Arg	args[7];	/* need 2 args (add 5 to be safe) */
    int	n = 0;
    String	title_str = NULL;
    
    if (instance->moddialog == NULL) {
        title_str = XtNewString(catgets(Dtb_project_catd, 2, 1, "Properties"));
        n = 0;
        XtSetArg(args[n], XmNallowShellResize, True);  ++n;
        XtSetArg(args[n], XmNtitle, title_str);  ++n;
        instance->moddialog =
            XmCreateDialogShell(parent,
                "dtb_sdtfprop_moddialog", args, n);
        XtFree(title_str);
        title_str = NULL;
    }
    if (instance->moddialog == NULL)
        return -1;

    if (instance->moddialog_shellform == NULL) {
        instance->moddialog_shellform =
            XtVaCreateWidget("dtb_sdtfprop_moddialog_shellform",
                xmFormWidgetClass,
                instance->moddialog,
                XmNnoResize, True,
                XmNdefaultPosition, False,
                XmNmarginHeight, 0,
                XmNmarginWidth, 0,
                NULL);
    }
    if (instance->moddialog_shellform == NULL)
        return -1;

    if (instance->moddialog_panedwin == NULL) {
        instance->moddialog_panedwin =
            XtVaCreateWidget("moddialog_panedwin",
                xmPanedWindowWidgetClass,
                instance->moddialog_shellform,
                XmNbottomOffset, 0,
                XmNbottomPosition, 100,
                XmNbottomAttachment, XmATTACH_POSITION,
                XmNrightOffset, 0,
                XmNrightPosition, 100,
                XmNrightAttachment, XmATTACH_POSITION,
                XmNleftOffset, 0,
                XmNleftPosition, 0,
                XmNleftAttachment, XmATTACH_POSITION,
                XmNtopOffset, 0,
                XmNtopPosition, 0,
                XmNtopAttachment, XmATTACH_POSITION,
                XmNsashWidth, 1,
                XmNsashHeight, 1,
                XmNspacing, 0,
                XmNmarginHeight, 0,
                XmNmarginWidth, 0,
                NULL);
    }
    if (instance->moddialog_panedwin == NULL)
        return -1;

    if (instance->moddialog_form == NULL) {
        instance->moddialog_form =
            XtVaCreateWidget("moddialog_form",
                xmFormWidgetClass,
                instance->moddialog_panedwin,
                XmNallowResize, True,
                XmNmarginHeight, 0,
                XmNmarginWidth, 0,
                XmNresizePolicy, XmRESIZE_ANY,
                NULL);
    }
    if (instance->moddialog_form == NULL)
        return -1;

    
    /*
     * Call user (Post-)Create procs
     */
    ModDialogPostCreateProc_CB(instance->moddialog, (XtPointer)NULL, (XtPointer)instance);
    return 0;
}



