void 
doubleClickCB(
    Widget widget,
    XtPointer ,
    XtPointer callData
)
{
    /*** DTB_USER_CODE_START vvv Add C variables and code below vvv ***/

    XmListCallbackStruct *cbs = (XmListCallbackStruct *)callData;
    AutoFilingCmd *cmd_ptr;

    /*** DTB_USER_CODE_END   ^^^ Add C variables and code above ^^^ ***/
    
    /*** DTB_USER_CODE_START vvv Add C code below vvv ***/

    // We do this temporarily to get around selection problem.
    // If in Multiple select mode, the selection gets added to the select list vs.
    // deselecting all selected items and just selecting the item that was double
    // clicked on, which is what we want.
    XtVaSetValues (widget,
	    XmNselectionPolicy, XmEXTENDED_SELECT,
	    NULL);

    if (cbs->item)
	XmListSelectItem(widget, cbs->item, 0);

    XtVaGetValues(widget, XmNuserData, &cmd_ptr, NULL);
    cmd_ptr->buttonCmd(AF_MAILBOX_OPEN);

    // Set the selection back
    XtVaSetValues (widget,
	    XmNselectionPolicy, XmMULTIPLE_SELECT,
	    NULL);

    /*** DTB_USER_CODE_END   ^^^ Add C code above ^^^ ***/
}



