void
msg_filing_browse_cb(void *client_data, char *selection)
{
    if (!selection) return;
    struct OpenStruct *os = (struct OpenStruct*)client_data;
    MoveMenuListUiItem *cmd_ptr = (MoveMenuListUiItem*)os->command;
    if (cmd_ptr != NULL) {
    	CustomSelectFileCmd *cd = (CustomSelectFileCmd*)cmd_ptr->getClientData();
    	if (cd->isLocal())
		cmd_ptr->handleBrowseButtonPress(selection, NULL);
	else {
		char *server = cd->getServerValue();
		cmd_ptr->handleBrowseButtonPress(selection, server);
		free(server);
	}
    }

}
